function Themes () {
    SelectionMenuGamemode.x += 200
    miniMenu.setButtonEventsEnabled(SelectionMenuGamemode, false)
    Thememenu = miniMenu.createMenu(
    miniMenu.createMenuItem("Basic Black"),
    miniMenu.createMenuItem("LowFi Circles"),
    miniMenu.createMenuItem("Early Background release"),
    miniMenu.createMenuItem("Back")
    )
    miniMenu.setTitle(Thememenu, "Themes")
    miniMenu.setFrame(Thememenu, assets.image`MenuFrame0`)
    miniMenu.setStyleProperty(Thememenu, miniMenu.StyleKind.Default, miniMenu.StyleProperty.Background, 15)
    miniMenu.setStyleProperty(Thememenu, miniMenu.StyleKind.Default, miniMenu.StyleProperty.Foreground, 2)
    miniMenu.setStyleProperty(Thememenu, miniMenu.StyleKind.Selected, miniMenu.StyleProperty.Background, 2)
    miniMenu.setStyleProperty(Thememenu, miniMenu.StyleKind.Selected, miniMenu.StyleProperty.Foreground, 1)
    miniMenu.onButtonPressed(Thememenu, miniMenu.Button.A, function (selection, selectedIndex) {
        if (selection == "Basic Black") {
            Themeselection = 0
            game.splash("Set")
        } else if (selection == "LowFi Circles") {
            Themeselection = 1
            game.splash("Set")
        } else if (selection == "Early Background release") {
            Themeselection = 2
            game.splash("Set")
        } else if (selection == "Back") {
            sprites.destroy(Thememenu)
            SelectionMenuGamemode.x += -200
            miniMenu.setButtonEventsEnabled(SelectionMenuGamemode, true)
        } else {
        	
        }
    })
}
sprites.onOverlap(SpriteKind.HealBullet, SpriteKind.Enemy, function (sprite, otherSprite) {
    _3HealthBar.value += sprites.readDataNumber(sprite, "Damage") / 10
    if (sprite.image.equals(assets.image`DrillProjectile`)) {
        if (!(sprites.readDataBoolean(otherSprite, "HitRecently"))) {
            sprites.setDataBoolean(otherSprite, "HitRecently", true)
            statusbars.getStatusBarAttachedTo(StatusBarKind.EnemyHealth, otherSprite).value += 0 - sprites.readDataNumber(sprite, "Damage")
            timer.background(function () {
                timer.after(150, function () {
                    sprites.setDataBoolean(otherSprite, "HitRecently", false)
                })
            })
        }
    } else {
        sprites.destroy(sprite)
        statusbars.getStatusBarAttachedTo(StatusBarKind.EnemyHealth, otherSprite).value += 0 - sprites.readDataNumber(sprite, "Damage")
    }
    if (statusbars.getStatusBarAttachedTo(StatusBarKind.EnemyHealth, otherSprite).value <= 0) {
        sprites.destroy(otherSprite)
    }
})
function DefineShape (Image2: Image, Health: number, Speed: number, Name: string, BodyDescription: string, WeaponDescription: string) {
    BodyImagesArray.push(Image2)
    BodyHealthArray.push(Health)
    BodySpeedArray.push(Speed)
    BodyNamesArray.push(Name)
    BodyDescriptionsArray.push(BodyDescription)
    WeaponDescriptionsArray.push(WeaponDescription)
}
function WeaponSelect () {
    SelectionMenuWeapons = miniMenu.createMenuFromArray(SelectionMenuBodiesArray)
    miniMenu.setFrame(SelectionMenuWeapons, assets.image`MenuFrame`)
    SelectionMenuWeapons.setPosition(81, scene.screenHeight() / 2)
    SelectionMenuText.setText("Select a Weapon.")
    miniMenu.onButtonPressed(SelectionMenuWeapons, miniMenu.Button.A, function (selection, selectedIndex) {
        _1Weapon = selectedIndex
        SelectionMenuCombinationSprite.image.drawTransparentImage(BodyImagesArray[selectedIndex], 0, 0)
        miniMenu.close(SelectionMenuWeapons)
        Confirmation()
    })
    miniMenu.onButtonPressed(SelectionMenuWeapons, miniMenu.Button.B, function (selection, selectedIndex) {
        game.showLongText("" + selection + "           " + WeaponDescriptionsArray[selectedIndex], DialogLayout.Center)
    })
}
